var productsArrayLength,isMobile=window.matchMedia("only screen and (max-width: 768px)").matches,increaseFor=isMobile?1:3,number=0,pictureNumber=0;function loadPicture(i){$(".pictures-list").html(""),$.getJSON("./storage/products.json",function(e){productsArrayLength||(productsArrayLength=e.length);for(var r=i;r<=i+increaseFor;r++)$("<figure class='picture-item'><img src='img/"+e[r].image+"' /><figcaption>"+e[r].name+"</figcaption></figure>").appendTo(".pictures-list")})}$(document).ready(function(){loadPicture(pictureNumber),$("#next-img").click(function(){pictureNumber<productsArrayLength-1-increaseFor&&loadPicture(pictureNumber+=1)}),$("#prev-img").click(function(){0<pictureNumber&&loadPicture(pictureNumber-=1)})});